#include <iostream>#include <vector>#include <set>void PrintEven(const std::vector<int>& v) {	for (std::vector<int>::const_iterator it = v.cbegin(); it != v.cend(); ++it) {		if (*it % 2 == 0)			std::cout << *it << ' ';	}}void PrintOdd(const std::set<int>& s) {	for (std::set<int>::iterator it = s.begin(); it != s.end(); ++it) {		if (*it % 2 == 1)			std::cout << *it << ' ';	}}int main() {	const std::vector<int> v{ 4, 8, 15, 16, 23, 42 };	const std::set<int> s{ 4, 8, 15, 16, 23, 42 };	for (std::vector<int>::const_iterator it = v.begin(); it != v.end(); it++) {		std::cout << *it << " ";	}	std::cout << std::endl;	PrintEven(v);	std::cout << std::endl;	PrintOdd(s);}